// src/App.js
import React, { Suspense, lazy } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import { ThemeProvider } from './contexts/ThemeContext';

// Only import Navigation directly as it's needed immediately
import Navigation from './components/common/Navigation';
import PrivateRoute from './components/common/PrivateRoute';

// Lazy load all other components
const AdminDashboard = lazy(() => import('./components/admin/AdminDashboard'));
const PortfolioManager = lazy(() => import('./components/admin/PortfolioManager'));
const BlogPostManager = lazy(() => import('./components/admin/BlogPostManager'));
const PortfolioList = lazy(() => import('./components/portfolio/PortfolioList'));
const BlogList = lazy(() => import('./components/blog/BlogList'));
const Login = lazy(() => import('./components/auth/Login'));
const Register = lazy(() => import('./components/auth/Register'));
const PasswordReset = lazy(() => import('./components/auth/PasswordReset'));
const EmailVerification = lazy(() => import('./components/auth/EmailVerification'));

// Loading component
const LoadingSpinner = () => (
  <div className="flex items-center justify-center min-h-screen">
    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-gray-900"></div>
  </div>
);

// Error Boundary Component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error('React Error Boundary:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
          <div className="text-center p-8 bg-white rounded-lg shadow-md">
            <h1 className="text-2xl font-bold text-red-600 mb-4">
              Something went wrong
            </h1>
            <pre className="text-sm text-gray-600 bg-gray-100 p-4 rounded">
              {this.state.error?.message}
            </pre>
            <button
              className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              onClick={() => window.location.reload()}
            >
              Reload Page
            </button>
          </div>
        </div>
      );
    }
    return this.props.children;
  }
}

function App() {
  return (
    <ErrorBoundary>
      <AuthProvider>
        <ThemeProvider>
          <Router>
            <div className="min-h-screen bg-gray-100">
              <Navigation />
              <Suspense fallback={<LoadingSpinner />}>
                <main className="container mx-auto px-4 py-8">
                  <Routes>
                    {/* Public Routes */}
                    <Route path="/" element={<PortfolioList />} />
                    <Route path="/portfolio" element={<PortfolioList />} />
                    <Route path="/blog" element={<BlogList />} />

                    {/* Auth Routes */}
                    <Route path="/login" element={<Login />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/password-reset" element={<PasswordReset />} />
                    <Route 
                      path="/verify-email/:token" 
                      element={<EmailVerification />} 
                    />

                    {/* Protected Admin Routes */}
                    <Route
                      path="/admin/dashboard"
                      element={
                        <PrivateRoute>
                          <AdminDashboard />
                        </PrivateRoute>
                      }
                    />
                    <Route
                      path="/admin/portfolio"
                      element={
                        <PrivateRoute>
                          <PortfolioManager />
                        </PrivateRoute>
                      }
                    />
                    <Route
                      path="/admin/blog-posts"
                      element={
                        <PrivateRoute>
                          <BlogPostManager />
                        </PrivateRoute>
                      }
                    />

                    {/* 404 Route */}
                    <Route
                      path="*"
                      element={
                        <div className="text-center py-12">
                          <h2 className="text-2xl font-bold text-gray-900">
                            Page Not Found
                          </h2>
                          <p className="mt-2 text-gray-600">
                            The page you're looking for doesn't exist.
                          </p>
                        </div>
                      }
                    />
                  </Routes>
                </main>
              </Suspense>
            </div>
          </Router>
        </ThemeProvider>
      </AuthProvider>
    </ErrorBoundary>
  );
}

export default App;
