import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import { ThemeProvider } from './contexts/ThemeContext';
// Updated import paths to match lowercase folder names
import Navigation from './components/common/Navigation';
import PrivateRoute from './components/common/PrivateRoute';
import AdminDashboard from './components/admin/AdminDashboard';
import PortfolioManager from './components/admin/PortfolioManager';
import BlogPostManager from './components/admin/BlogPostManager';
import PortfolioList from './components/portfolio/PortfolioList';
import BlogList from './components/blog/BlogList';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import PasswordReset from './components/auth/PasswordReset';
import EmailVerification from './components/auth/EmailVerification';

function App() {
    return (
        <AuthProvider>
            <ThemeProvider>
                <Router>
                    <Navigation />
                    <Routes>
                        {/* Auth Routes */}
                        <Route path="/login" element={<Login />} />
                        <Route path="/register" element={<Register />} />
                        <Route path="/password-reset" element={<PasswordReset />} />
                        <Route path="/verify-email/:token" element={<EmailVerification />} />

                        {/* Admin Routes (Protected) */}
                        <Route path="/admin/dashboard" element={<PrivateRoute><AdminDashboard /></PrivateRoute>} />
                        <Route path="/admin/portfolio" element={<PrivateRoute><PortfolioManager /></PrivateRoute>} />
                        <Route path="/admin/blog-posts" element={<PrivateRoute><BlogPostManager /></PrivateRoute>} />

                        {/* Portfolio and Blog Routes */}
                        <Route path="/portfolio" element={<PortfolioList />} />
                        <Route path="/blog" element={<BlogList />} />

                        {/* Default Route */}
                        <Route path="/" element={<PortfolioList />} />
                    </Routes>
                </Router>
            </ThemeProvider>
        </AuthProvider>
    );
}

export default App;
